LIBRARY IEEE;
USE IEEE.STD_LOGIC_1164.ALL;
USE IEEE.NUMERIC_STD.ALL;
LIBRARY ECE411;
USE ECE411.LC3B_TYPES.ALL;

ENTITY DRAM128 IS
PORT( 
	ADDRESS  : IN     LC3B_WORD;
	DATAIN   : IN     LC3B_OWORD;
	MREAD_L  : IN     STD_LOGIC;
	MWRITE_L : IN     STD_LOGIC;
	RESET_L  : IN     STD_LOGIC;
	DATAOUT  : OUT    LC3B_OWORD;
	MRESP_H  : OUT    STD_LOGIC
);
-- DECLARATIONS
END DRAM128 ;

ARCHITECTURE UNTITLED OF DRAM128 IS
BEGIN
	-------------------------------------------------------------------
	VHDL_MEMORY : PROCESS (RESET_L, MREAD_L, MWRITE_L) 
	-------------------------------------------------------------------
	VARIABLE MEM : MEMORY_ARRAY_64K;
	VARIABLE INT_OLD_ADDRESS : INTEGER;
	VARIABLE INT_ADDRESS : INTEGER;
	BEGIN
		INT_ADDRESS := TO_INTEGER(UNSIGNED(ADDRESS(15 DOWNTO 4)) * 16);
		IF RESET_L = '0' THEN
			MRESP_H <= '0';
			MYDRAMINIT_64K(MEM);
		ELSE
			IF ((INT_ADDRESS >= 0) AND (INT_ADDRESS <= 65535)) THEN
				IF (MWRITE_L'EVENT AND (MWRITE_L'LAST_VALUE = '0') AND (INT_ADDRESS /= INT_OLD_ADDRESS)) THEN
					ASSERT FALSE 
					REPORT "MEMORY WRITE TIMING ERROR"
					SEVERITY NOTE;
				END IF;
				IF (MREAD_L'EVENT AND (MREAD_L'LAST_VALUE = '0') AND (INT_ADDRESS /= INT_OLD_ADDRESS)) THEN
					ASSERT FALSE 
					REPORT "MEMORY READ TIMING ERROR"
					SEVERITY NOTE;
				END IF;
				IF (MREAD_L = '0' AND MWRITE_L = '1') THEN
					DATAOUT(7 DOWNTO 0) <= MEM(INT_ADDRESS) AFTER DELAY_128B_MEM;
					DATAOUT(15 DOWNTO 8) <= MEM(INT_ADDRESS + 1) AFTER DELAY_128B_MEM;
					DATAOUT(23 DOWNTO 16) <= MEM(INT_ADDRESS + 2) AFTER DELAY_128B_MEM;
					DATAOUT(31 DOWNTO 24) <= MEM(INT_ADDRESS + 3) AFTER DELAY_128B_MEM;
					DATAOUT(39 DOWNTO 32) <= MEM(INT_ADDRESS + 4) AFTER DELAY_128B_MEM;
					DATAOUT(47 DOWNTO 40) <= MEM(INT_ADDRESS + 5) AFTER DELAY_128B_MEM;
					DATAOUT(55 DOWNTO 48) <= MEM(INT_ADDRESS + 6) AFTER DELAY_128B_MEM;
					DATAOUT(63 DOWNTO 56) <= MEM(INT_ADDRESS + 7) AFTER DELAY_128B_MEM;
					DATAOUT(71 DOWNTO 64) <= MEM(INT_ADDRESS + 8) AFTER DELAY_128B_MEM;
					DATAOUT(79 DOWNTO 72) <= MEM(INT_ADDRESS + 9) AFTER DELAY_128B_MEM;
					DATAOUT(87 DOWNTO 80) <= MEM(INT_ADDRESS + 10) AFTER DELAY_128B_MEM;
					DATAOUT(95 DOWNTO 88) <= MEM(INT_ADDRESS + 11) AFTER DELAY_128B_MEM;
					DATAOUT(103 DOWNTO 96) <= MEM(INT_ADDRESS + 12) AFTER DELAY_128B_MEM;
					DATAOUT(111 DOWNTO 104) <= MEM(INT_ADDRESS + 13) AFTER DELAY_128B_MEM;
					DATAOUT(119 DOWNTO 112) <= MEM(INT_ADDRESS + 14) AFTER DELAY_128B_MEM;
					DATAOUT(127 DOWNTO 120) <= MEM(INT_ADDRESS + 15) AFTER DELAY_128B_MEM;
					MRESP_H <= '1' AFTER DELAY_128B_MEM, '0' AFTER DELAY_128B_MEM + CLOCK_PERIOD;
				ELSIF (MWRITE_L = '0' AND MREAD_L = '1') THEN				
					MEM(INT_ADDRESS) := DATAIN(7 DOWNTO 0);
					MEM(INT_ADDRESS + 1) := DATAIN(15 DOWNTO 8);
					MEM(INT_ADDRESS + 2) := DATAIN(23 DOWNTO 16);
					MEM(INT_ADDRESS + 3) := DATAIN(31 DOWNTO 24);
					MEM(INT_ADDRESS + 4) := DATAIN(39 DOWNTO 32);
					MEM(INT_ADDRESS + 5) := DATAIN(47 DOWNTO 40);
					MEM(INT_ADDRESS + 6) := DATAIN(55 DOWNTO 48);
					MEM(INT_ADDRESS + 7) := DATAIN(63 DOWNTO 56);
					MEM(INT_ADDRESS + 8) := DATAIN(71 DOWNTO 64);
					MEM(INT_ADDRESS + 9) := DATAIN(79 DOWNTO 72);
					MEM(INT_ADDRESS + 10) := DATAIN(87 DOWNTO 80);
					MEM(INT_ADDRESS + 11) := DATAIN(95 DOWNTO 88);
					MEM(INT_ADDRESS + 12) := DATAIN(103 DOWNTO 96);
					MEM(INT_ADDRESS + 13) := DATAIN(111 DOWNTO 104);
					MEM(INT_ADDRESS + 14) := DATAIN(119 DOWNTO 112);
					MEM(INT_ADDRESS + 15) := DATAIN(127 DOWNTO 120);
					MRESP_H <= '1' AFTER DELAY_128B_MEM, '0' AFTER DELAY_128B_MEM + CLOCK_PERIOD;				
				ELSIF (MWRITE_L = '0' AND MREAD_L = '0') THEN
					ASSERT FALSE 
					REPORT "ATTEMPTING TO READ AND WRITE DRAM SIMULTANEOUSLY."
					SEVERITY ERROR;
				END IF;
			ELSE
				ASSERT FALSE
				REPORT "INVALID ADDRESS SENT TO DRAM."
				SEVERITY WARNING;
			END IF;
		END IF;
	END PROCESS VHDL_MEMORY;
END UNTITLED;